package test.interfacUI.camera.activate;import edu.sfsu.cs.orange.ocr.*;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.view.View.OnClickListener;public class CameraActivateButton extends Activity{    Button cameraButton;	@Override	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.camerabutton);		cameraButton=(Button)findViewById(R.id.button0);		cameraButton.setOnClickListener(new OnClickListener() {						public void onClick(View v) {				Intent intent = new Intent();                intent.setClass(CameraActivateButton.this, CaptureActivity.class);                startActivity(intent);			}		});						 	}}